<?php

namespace Kaliop\ExoBundle\Repository;

use Doctrine\ORM\EntityRepository;

/**
 * ArticleRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class ArticleRepository extends EntityRepository
{
    public function findByAuthor($author) /* Retourne tous les articles d'un autheur */
    {
        return $this
            ->createQueryBuilder("a")
            ->where("a.author = :author")
            ->setParameter("author", $author)
            ->getQuery()
            ->getResult()
        ;
    }



    public function findByAuthorAndYear($author, $year) /* Retourne les articles triÃ©s par autheur et annÃ©e */
    {
        return $this
            ->createQueryBuilder("a")
            ->where("a.author = :author")
            ->setParameter("author", $author)
            ->andWhere("a.date < :year")
            ->setParameter("year", $year)
            ->orderBy("a.date", "DESC")
            ->getQuery()
            ->getResult()
        ;
    }



    public function getArticleWithImage()
    {
        return $this
            ->createQueryBuilder("a")
            ->leftJoin("a.image", "img")
            ->addSelect("img")
            ->getQuery()
            ->getResult()
        ;
    }
}
